package core_java;

public class ArithmeticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 40;
		int b = 30;
		System.out.println("a + b = " +(a+b));
		System.out.println("a - b = " +(a-b));
		System.out.println("a * b = " +(a*b));
		System.out.println("a / b = " +(a/b));
		System.out.println("a % b = " +(a%b));

	}

}
